import React from 'react';
import './ChessPuzzlesSection.css';

const ChessPuzzlesSection = () => {
  return (
    <section className="puzzles-section">
      <h2>Resolver Problemas de Ajedrez</h2>
      <div className="puzzle-container">
        <div className="chess-board">
          {/* Aquí irá el componente del tablero */}
        </div>
        <div className="puzzle-info">
          <p className="puzzle-hint">Mate en 2 movimientos</p>
          <blockquote>
            "Los problemas de ajedrez son el gimnasio de la mente"
            <footer>- Magnus Carlsen</footer>
          </blockquote>
        </div>
      </div>
    </section>
  );
};

export default ChessPuzzlesSection;
