import { Chess } from 'chess.js';

const puzzles = [
  {
    id: 1,
    moves: ["e4", "e5", "Nf3", "Nc6", "Bc4"],
    solution: ["Bxf7+", "Kxf7", "Nxe5+", "Ke8", "Qh5+", "g6", "Nxg6#"],
    turnToPlay: 'w'
  },
  {
    id: 2,
    moves: ["e4", "e5", "Nf3", "Nc6", "Bc4", "Nf6"],
    solution: ["Nxe5", "Nxe5", "d4", "Nc6", "d5", "Ne7", "Qxd7#"],
    turnToPlay: 'w'
  }
];

export const loadPuzzles = async () => {
  console.log('1. Iniciando carga de puzzles');
  return new Promise((resolve) => {
    setTimeout(() => {
      console.log('2. Puzzles cargados:', puzzles);
      resolve(puzzles);
    }, 500);
  });
};

export const getRandomPuzzle = (puzzles) => {
  console.log('3. Seleccionando puzzle aleatorio');
  const randomIndex = Math.floor(Math.random() * puzzles.length);
  const puzzle = puzzles[randomIndex];
  
  try {
    console.log('4. Puzzle seleccionado:', puzzle);
    const chess = new Chess();
    
    console.log('5. Aplicando movimientos:', puzzle.moves);
    puzzle.moves.forEach((move, index) => {
      const result = chess.move(move);
      console.log(`   Movimiento ${index + 1}: ${move} -> ${result ? 'OK' : 'FAIL'}`);
    });
    
    const finalPosition = chess.fen();
    console.log('6. Posición final:', finalPosition);
    
    return {
      ...puzzle,
      initialPosition: finalPosition
    };
  } catch (error) {
    console.error('7. Error al preparar el puzzle:', error);
    return null;
  }
};
