import { Chess } from 'chess.js';

const parsePGN = (pgnText) => {
  const puzzles = [];
  const games = pgnText.split('\n\n[Event');
  
  games.forEach((game, index) => {
    if (index === 0 && !game.startsWith('[Event')) {
      game = '[Event' + game;
    }
    
    try {
      const fenMatch = game.match(/\[FEN "(.*?)"\]/);
      const solutionMatch = game.match(/\[Solution "(.*?)"\]/);
      
      if (fenMatch && solutionMatch) {
        const fen = fenMatch[1];
        const solution = solutionMatch[1].split(' ').map(move => 
          move.endsWith('#') ? move.slice(0, -1) : move
        );
        
        const chess = new Chess();
        if (chess.load(fen)) {
          puzzles.push({
            id: index + 1,
            fen: fen,
            solution: solution,
            isMate: true,
            turnToPlay: 'w'
          });
          console.log(`Puzzle ${index + 1} cargado:`, {
            fen: fen,
            solution: solution
          });
        }
      }
    } catch (error) {
      console.error(`Error al procesar puzzle ${index + 1}:`, error);
    }
  });
  
  return puzzles;
};

export const loadPuzzlesFromPGN = async () => {
  try {
    console.log('Cargando puzzles...');
    const response = await fetch('/data/puzzles/mate-en-dos-processed.pgn');
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const pgnText = await response.text();
    console.log('Archivo PGN cargado, procesando puzzles...');
    const puzzles = parsePGN(pgnText);
    console.log(`${puzzles.length} puzzles procesados correctamente`);
    return puzzles;
  } catch (error) {
    console.error('Error loading puzzles:', error);
    return [];
  }
};

export const getRandomPuzzle = (puzzles) => {
  if (!puzzles || puzzles.length === 0) return null;
  const randomIndex = Math.floor(Math.random() * puzzles.length);
  return puzzles[randomIndex];
};
