import React, { useState, useEffect } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';
import { loadPuzzles, getRandomPuzzle } from '../../services/puzzleService';
import './Chessboard.css';

const ChessboardComponent = ({ onPuzzleMessage }) => {
  const [game] = useState(() => new Chess());
  const [currentPuzzle, setCurrentPuzzle] = useState(null);
  const [position, setPosition] = useState(game.fen());

  useEffect(() => {
    const loadRandomPuzzle = async () => {
      try {
        const puzzles = await loadPuzzles();
        if (puzzles && puzzles.length > 0) {
          const puzzle = getRandomPuzzle(puzzles);
          
          // Crear una nueva instancia de Chess para el puzzle
          const puzzleGame = new Chess();
          if (!puzzleGame.load(puzzle.initialPosition)) {
            console.error('Posici칩n inv치lida:', puzzle.initialPosition);
            return;
          }
          
          setPosition(puzzleGame.fen());
          setCurrentPuzzle(puzzle);
          
          const movesForMate = Math.ceil(puzzle.solution.length / 2);
          onPuzzleMessage(`${puzzle.turnToPlay === 'w' ? 'Blancas' : 'Negras'} juegan - Mate en ${movesForMate}`);
        }
      } catch (error) {
        console.error('Error al cargar el puzzle:', error);
      }
    };

    loadRandomPuzzle();
  }, [onPuzzleMessage]);

  const onDrop = (sourceSquare, targetSquare) => {
    try {
      const move = game.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: 'q'
      });

      if (move === null) return false;
      
      setPosition(game.fen());
      return true;
    } catch (error) {
      console.error('Error al mover:', error);
      return false;
    }
  };

  return (
    <div className="chessboard-container">
      <div className="board-wrapper">
        <Chessboard 
          position={position}
          onPieceDrop={onDrop}
          boardWidth={400}
          customDragLayer={false}
          boardOrientation={currentPuzzle?.turnToPlay === 'b' ? 'black' : 'white'}
        />
      </div>
    </div>
  );
};

export default ChessboardComponent;
