import { Chess } from 'chess.js';

const puzzles = [
  {
    id: 1,
    pgn: `[Event "Puzzle"]
[FEN "r1bqkb1r/pppp1ppp/2n2n2/4p3/2B1P3/5N2/PPPP1PPP/RNBQK2R w KQkq - 0 1"]

1. Bxf7+ Kxf7 2. Nxe5+ Ke8 3. Qh5+ g6 4. Nxg6#`,
    solution: ["Bxf7+", "Kxf7", "Nxe5+", "Ke8", "Qh5+", "g6", "Nxg6#"],
    turnToPlay: 'w',
    initialPosition: "r1bqkb1r/pppp1ppp/2n2n2/4p3/2B1P3/5N2/PPPP1PPP/RNBQK2R w KQkq - 0 1"
  },
  {
    id: 2,
    pgn: `[Event "Puzzle"]
[FEN "r1b1kb1r/pppp1ppp/2n2n2/4p3/2B1P3/2N2N2/PPPP1PPP/R1BQK2R w KQkq - 0 1"]

1. Nxe5 Nxe5 2. d4 Nc6 3. d5 Ne7 4. Qxd7#`,
    solution: ["Nxe5", "Nxe5", "d4", "Nc6", "d5", "Ne7", "Qxd7#"],
    turnToPlay: 'w',
    initialPosition: "r1b1kb1r/pppp1ppp/2n2n2/4p3/2B1P3/2N2N2/PPPP1PPP/R1BQK2R w KQkq - 0 1"
  }
];

export const loadPuzzles = async () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(puzzles);
    }, 500);
  });
};

export const getRandomPuzzle = (puzzles) => {
  const randomIndex = Math.floor(Math.random() * puzzles.length);
  const puzzle = puzzles[randomIndex];
  
  // Usar directamente la posición inicial
  return {
    ...puzzle,
    initialPosition: puzzle.initialPosition,
    moves: puzzle.solution
  };
};
