import React, { useState, useEffect, useRef } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';
import { loadPuzzles, getRandomPuzzle } from '../../services/puzzleService';
import './Chessboard.css';

const ChessboardComponent = ({ onPuzzleMessage }) => {
  const [game, setGame] = useState(null);
  const [currentPuzzle, setCurrentPuzzle] = useState(null);
  const [solutionIndex, setSolutionIndex] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [boardKey, setBoardKey] = useState(0);
  const isFirstRender = useRef(true);

  useEffect(() => {
    if (isFirstRender.current) {
      console.log('Primera carga del componente');
      loadRandomPuzzle();
      isFirstRender.current = false;
    }
  }, []);

  const setupPosition = (puzzle) => {
    const newGame = new Chess();
    console.log('Configurando nueva posición');
    
    // Aplicar los movimientos uno por uno
    for (const move of puzzle.moves) {
      const result = newGame.move(move);
      if (!result) {
        console.error('Error al aplicar movimiento:', move);
        return null;
      }
    }
    
    console.log('Posición final configurada:', newGame.fen());
    return newGame;
  };

  const loadRandomPuzzle = async () => {
    console.log('A. Iniciando carga de puzzle');
    setIsLoading(true);
    try {
      const puzzles = await loadPuzzles();
      console.log('B. Puzzles recibidos:', puzzles);

      if (puzzles && puzzles.length > 0) {
        const puzzle = getRandomPuzzle(puzzles);
        console.log('C. Puzzle seleccionado:', puzzle);

        if (!puzzle) {
          console.error('D. Error: No se pudo cargar el puzzle');
          return;
        }

        const newGame = setupPosition(puzzle);
        if (!newGame) {
          console.error('Error al configurar la posición');
          return;
        }

        setGame(newGame);
        setCurrentPuzzle(puzzle);
        setSolutionIndex(0);
        setBoardKey(prev => prev + 1);
        
        const movesForMate = Math.ceil(puzzle.solution.length / 2);
        onPuzzleMessage(`Tu turno - Encuentra el mate en ${movesForMate}`);
      }
    } catch (error) {
      console.error('H. Error en loadRandomPuzzle:', error);
    } finally {
      console.log('I. Finalizando carga');
      setIsLoading(false);
    }
  };

  const onDrop = (sourceSquare, targetSquare) => {
    if (!game || isLoading) return false;

    try {
      const move = game.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: 'q'
      });

      if (move === null) return false;

      if (currentPuzzle && currentPuzzle.solution[solutionIndex] === move.san) {
        onPuzzleMessage('¡Correcto! Espera la respuesta...');
        
        setTimeout(() => {
          const opponentMove = currentPuzzle.solution[solutionIndex + 1];
          game.move(opponentMove);
          setGame(new Chess(game.fen()));
          setBoardKey(prev => prev + 1);
          setSolutionIndex(solutionIndex + 2);

          if (solutionIndex + 2 >= currentPuzzle.solution.length) {
            onPuzzleMessage('¡Puzzle completado! Cargando nuevo puzzle...');
            setTimeout(loadRandomPuzzle, 2000);
          } else {
            onPuzzleMessage('Tu turno - Continúa con el mate');
          }
        }, 500);
      } else {
        game.undo();
        onPuzzleMessage('Movimiento incorrecto. Intenta de nuevo');
        return false;
      }

      return true;
    } catch (error) {
      console.error('Error al mover:', error);
      return false;
    }
  };

  if (isLoading || !game) {
    return <div>Cargando puzzle...</div>;
  }

  return (
    <div className="chessboard-container">
      <div className="board-wrapper">
        {game && (
          <Chessboard 
            key={boardKey}
            position={game.fen()}
            onPieceDrop={onDrop}
            boardWidth={400}
            customDragLayer={false}
            boardOrientation={currentPuzzle?.turnToPlay === 'b' ? 'black' : 'white'}
          />
        )}
      </div>
    </div>
  );
};

export default ChessboardComponent;
