import { readFileSync } from 'fs';
import { join } from 'path';

export const loadPuzzlesFromPGN = async () => {
  try {
    const pgnFile = await fetch('/data/puzzles/mate-in-two.pgn');
    const pgnText = await pgnFile.text();
    return parsePGN(pgnText);
  } catch (error) {
    console.error('Error loading puzzles:', error);
    return [];
  }
};
