import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';
import { loadPuzzlesFromPGN } from '../../services/puzzleService';
import './Chessboard.css';

const ChessboardComponent = () => {
  const [game, setGame] = useState(() => {
    const chess = new Chess();
    chess.clear();
    return chess;
  });
  const [currentPuzzle, setCurrentPuzzle] = useState(null);
  const [solutionIndex, setSolutionIndex] = useState(0);
  const [isComplete, setIsComplete] = useState(false);
  const [message, setMessage] = useState('Cargando puzzle...');
  const [position, setPosition] = useState('8/8/8/8/8/8/8/8 w - - 0 1');

  const gameRef = useRef(null);

  const loadRandomPuzzle = useCallback(async () => {
    try {
      const puzzles = await loadPuzzlesFromPGN();
      console.log('Puzzles cargados:', puzzles);
      
      if (puzzles && puzzles.length > 0) {
        const puzzle = puzzles[0];
        console.log('Cargando puzzle:', puzzle);
        
        const newGame = new Chess();
        try {
          newGame.clear();
          
          const success = newGame.load(puzzle.fen);
          
          if (success) {
            const newPosition = puzzle.fen;
            console.log('PosiciÃ³n cargada correctamente:', {
              fen: newPosition,
              turno: newGame.turn(),
              movimientos: puzzle.solution
            });
            
            setGame(newGame);
            setPosition(newPosition);
            setCurrentPuzzle(puzzle);
            setSolutionIndex(0);
            setIsComplete(false);
            setMessage('Tu turno - Encuentra el mate en dos');
          } else {
            throw new Error('Error al cargar FEN');
          }
        } catch (error) {
          console.error('Error al cargar FEN:', error);
          setMessage('Error al cargar la posiciÃ³n');
        }
      } else {
        setMessage('No hay puzzles disponibles');
      }
    } catch (error) {
      console.error('Error al cargar puzzle:', error);
      setMessage('Error al cargar el puzzle');
    }
  }, []);

  useEffect(() => {
    loadRandomPuzzle();
  }, []);

  useEffect(() => {
    if (game && game.fen()) {
      setPosition(game.fen());
    }
  }, [game]);

  const onDrop = (sourceSquare, targetSquare) => {
    if (isComplete) return false;

    try {
      const gameCopy = new Chess(game.fen());
      const move = gameCopy.move({
        from: sourceSquare,
        to: targetSquare,
        promotion: 'q'
      });

      if (!move) return false;

      console.log('Movimiento realizado:', {
        from: sourceSquare,
        to: targetSquare,
        san: move.san,
        esperado: currentPuzzle.solution[solutionIndex],
        isCheck: gameCopy.isCheck(),
        isCheckmate: gameCopy.isCheckmate()
      });

      const expectedMove = currentPuzzle.solution[solutionIndex].replace('#', '');
      const actualMove = move.san.replace('#', '');
      const isMateMove = currentPuzzle.solution[solutionIndex].includes('#');

      if (actualMove === expectedMove) {
        setGame(gameCopy);
        setPosition(gameCopy.fen());
        
        if (isMateMove && gameCopy.isCheckmate()) {
          setIsComplete(true);
          setMessage('Â¡Excelente! Â¡Has encontrado el mate! ðŸŽ‰');
          return true;
        } else if (isMateMove && !gameCopy.isCheckmate()) {
          setMessage('Â¡El movimiento es correcto pero no es mate!');
          return false;
        }

        setMessage('Â¡Correcto! Espera la respuesta...');
        
        setTimeout(() => {
          const opponentMove = currentPuzzle.solution[solutionIndex + 1];
          if (!opponentMove) return;

          const oppGame = new Chess(gameCopy.fen());
          const result = oppGame.move(opponentMove.replace('#', ''));

          if (result) {
            setGame(oppGame);
            setPosition(oppGame.fen());
            setSolutionIndex(prev => prev + 2);
            setMessage('Tu turno - Da mate');
          }
        }, 500);

        return true;
      }

      setMessage('Movimiento incorrecto. Intenta de nuevo');
      return false;

    } catch (error) {
      console.error('Error en movimiento:', error);
      return false;
    }
  };

  return (
    <div className="chessboard-container">
      <Chessboard 
        position={position}
        onPieceDrop={onDrop}
        boardOrientation="white"
        arePremovesAllowed={false}
        customBoardStyle={{
          borderRadius: '4px',
          boxShadow: '0 2px 10px rgba(0, 0, 0, 0.5)'
        }}
      />
      <div className="puzzle-status">
        <p className={isComplete ? 'success-message' : 'message'}>
          {message}
        </p>
        {currentPuzzle && !isComplete && (
          <p className="move-counter">
            Movimiento {Math.floor(solutionIndex/2) + 1} de {Math.ceil(currentPuzzle.solution.length/2)}
          </p>
        )}
      </div>
    </div>
  );
};

export default ChessboardComponent;
