import { Chess } from 'chess.js';

const puzzles = [
  {
    id: 1,
    moves: ["e4", "e5", "Nf3", "Nc6", "Bc4"],  // Posición inicial seguida de movimientos
    solution: ["Bxf7+", "Kxf7", "Nxe5+", "Ke8", "Qh5+", "g6", "Nxg6#"],
    turnToPlay: 'w'
  },
  {
    id: 2,
    moves: ["e4", "e5", "Nf3", "Nc6", "Bc4", "Nf6"],
    solution: ["Nxe5", "Nxe5", "d4", "Nc6", "d5", "Ne7", "Qxd7#"],
    turnToPlay: 'w'
  }
];

export const loadPuzzles = async () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(puzzles);
    }, 500);
  });
};

export const getRandomPuzzle = (puzzles) => {
  const randomIndex = Math.floor(Math.random() * puzzles.length);
  const puzzle = puzzles[randomIndex];
  
  try {
    const chess = new Chess();
    
    // Hacer los movimientos para llegar a la posición inicial del puzzle
    puzzle.moves.forEach(move => {
      const result = chess.move(move);
      if (!result) {
        throw new Error(`Movimiento inválido: ${move}`);
      }
    });
    
    console.log('Posición del puzzle:', chess.fen());
    
    return {
      ...puzzle,
      initialPosition: chess.fen()
    };
  } catch (error) {
    console.error('Error al preparar el puzzle:', error);
    return null;
  }
};
