import { Chess } from 'chess.js';

const puzzles = [
  {
    id: 1,
    fen: "2r3k1/pp3pp1/2n2q2/3B2b1/8/2P5/PP3PPP/3R2K1 w - - 0 1",
    solution: ["Rxd8+", "Kxd8", "Bxf7"],
    turnToPlay: 'w'
  },
  {
    id: 2,
    fen: "4kb1r/p2n1ppp/4q3/4p1B1/4P3/1Q6/PPP2PPP/2KR4 w k - 0 1",
    solution: ["Qxb7", "Qxb7", "Rd8#"],
    turnToPlay: 'w'
  }
];

export const loadPuzzles = async () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(puzzles);
    }, 500);
  });
};

export const getRandomPuzzle = (puzzles) => {
  const randomIndex = Math.floor(Math.random() * puzzles.length);
  const puzzle = puzzles[randomIndex];
  
  try {
    const chess = new Chess();
    const isValid = chess.load(puzzle.fen);
    console.log('Verificando FEN:', puzzle.fen, 'Es v치lido:', isValid);
    
    if (!isValid) {
      console.error('FEN inv치lido:', puzzle.fen);
      return null;
    }
    
    return {
      ...puzzle,
      initialPosition: puzzle.fen
    };
  } catch (error) {
    console.error('Error al validar FEN:', error);
    return null;
  }
};
